interface TitleProps {
  /**
   * `heading` should be a string and
   * will be rendered as an h1 element
   */
  heading: string
  /**
   * `subHeading` should be a string
   * and will be renderd as an h2
   * element
   */
  subHeading: string
  /**
   * `on` will determing whether or not
   * the Heading elements are rendered
   */
  on: boolean
}

export { TitleProps }
